                .select("id, title, user_id, comment_count, view_count, created") //å¯¹æ–‡ç« çš„å±æ€§è¿›è¡Œç­›é€‰ï¼ŒåŠ å¿«æŸ¥è¯¢é€Ÿç‡
        );

        //2.åˆå§‹åŒ–ã€è¿‘7å¤©æ–‡ç« ã€‘çš„æ€»è¯„è®ºé‡ï¼ˆå…ˆä½¿ç”¨SortedSeté›†åˆå¯¹ã€æ’è¡Œæ¦œ7å¤©å†…å…¨éƒ¨æ–‡ç« ã€‘è¿›è¡Œzaddæ“ä½œï¼Œå¹¶è®¾ç½®å®ƒä»¬expireä¸º7å¤©ï¼›å†ä½¿ç”¨Hashå“ˆå¸Œè¡¨å¯¹ã€æ’è¡Œæ¦œ7å¤©å†…å…¨éƒ¨æ–‡ç« ã€‘è¿›è¡Œhexistsåˆ¤æ–­ï¼Œå†hsetç¼“å­˜æ“ä½œï¼‰
        for (Post post : posts) {
            //1.æ·»åŠ addâ€”â€”|day:rank:20210202--0208|ï¼Œå°†ã€è¿‘7å¤©æ–‡ç« ã€‘åˆ›å»ºæ—¥æœŸæ—¶é—´ä½œä¸ºkeyå€¼ï¼Œæ¯ç¯‡æ–‡ç« å¯¹åº”çš„idä½œä¸ºå®ƒçš„valueå€¼ï¼Œæ¯ç¯‡æ–‡ç« å¯¹åº”çš„è¯„è®ºcommentä½œä¸ºå®ƒçš„scoreå€¼ï¼Œå¹¶ä½¿ç”¨redisçš„å·¥å…·ç±»ï¼ˆRedisUtilï¼‰ï¼Œå¯¹æ–‡ç« çš„å…·ä½“å±æ€§è¿›è¡ŒzSet()ç¼“å­˜æ“ä½œ
            String zKey = "day:rank:" + DateUtil.format(post.getCreated(), DatePattern.PURE_DATE_FORMAT);
            redisUtil.zSet(zKey, post.getId(), post.getCommentCount());//é˜…è¯»redisUtilå·¥å…·ç±»ï¼Œå¯çŸ¥zSetç­‰åŒäºzadd

            //2.è¿‡æœŸexpireâ€”â€”|day:rank:20210202--0208|ï¼Œè®©ã€è¿‘7å¤©æ–‡ç« ã€‘çš„keyè¿‡æœŸï¼š 7-ï¼ˆå½“å‰æ—¶é—´-åˆ›å»ºæ—¶é—´ï¼‰= è¿‡æœŸæ—¶é—´
            long expireTime = (7 - DateUtil.between(new Date(), post.getCreated(), DateUnit.DAY)) * 24 * 60 * 60;
            redisUtil.expire(zKey, expireTime);

            //3.ç¼“å­˜â€”â€”|day:rank:post:1~16|ï¼Œç¼“å­˜ã€è¿‘7å¤©æ–‡ç« ã€‘çš„ä¸€äº›åŸºæœ¬ä¿¡æ¯ï¼Œä¾‹å¦‚æ–‡ç« idï¼Œæ ‡é¢˜titleï¼Œè¯„è®ºæ•°é‡ï¼Œä½œè€…ä¿¡æ¯...æ–¹ä¾¿è®¿é—®ã€è¿‘7å¤©æ–‡ç« ã€‘æ—¶ï¼Œç›´æ¥redisï¼Œè€ŒéMySQL
            //3.1å…ˆå¯¹æ–‡ç« è¿›è¡ŒEXISTSåˆ¤æ–­å…¶ç¼“å­˜æ˜¯å¦å­˜åœ¨
            String hKey = "day:rank:post:" + post.getId();
            if (!redisUtil.hasKey(hKey)) {
                //3.2å¦‚æœfalseä¸å­˜åœ¨ï¼Œåˆ™å†hsetç¼“å­˜æ“ä½œ
                redisUtil.hset(hKey, "post-id", post.getId(), expireTime);
                redisUtil.hset(hKey, "post-title", post.getTitle(), expireTime);
                redisUtil.hset(hKey, "post-commentCount", post.getCommentCount(), expireTime);
                redisUtil.hset(hKey, "post-viewCount", post.getViewCount(), expireTime);
            }
        }

        //3.å¯¹ã€è¿‘7å¤©æ–‡ç« ã€‘åšå¹¶é›†è¿ç®—ï¼ˆzUnionAndStoreï¼‰ï¼Œ å¹¶ä½¿ç”¨æ ¹æ®è¯„è®ºé‡çš„æ•°é‡ä»å¤§åˆ°å°è¿›è¡Œå±•ç¤ºï¼ˆzrevrangeï¼‰
        String currentKey = "day:rank:" + DateUtil.format(new Date(), DatePattern.PURE_DATE_FORMAT);
        List<String> otherKeys = new ArrayList<>();
        for (int i = -6; i < 0; i++) {
            String temp = "day:rank:" + DateUtil.format(DateUtil.offsetDay(new Date(), i), DatePattern.PURE_DATE_FORMAT);
            otherKeys.add(temp);
